
; Samus drawing, movement, projectile handling, minimap

org $908000


;;; $8000: Animate Samus ;;;
AnimateSamus:
; Calls code to check FX for liquid physics (which affects the Samus animation frame buffer variable)
; That code also handles lava/acid damage and water splash / air bubble graphics
; Some special case code for keeping to frame 1 of neutral jump animation whilst rising
; Decrement the Samus animation frame timer,
; if zero then Samus animation frame is incremented and the Samus animation delay table is processed
; If an instruction is encountered in the animation delay table,
; some handling for speed booster running is called that deals with incrementing the speed booster stage
; If speed booster handling is not applicable, then the animation delay instruction is processed,
; which may cause looping or branching of the animation

; The actual drawing of Samus (using the Samus pose and Samus animation frame) happens elsewhere (draw Samus routines $85E2..8A4B)
    PHP                                                                  ;908000;
    REP #$30                                                             ;908001;
    JSL.L Get_Samus_BottomTop_Boundary                                   ;908003;
    LDA.W FX_Type                                                        ;908007;
    AND.W #$000F                                                         ;90800A;
    TAX                                                                  ;90800D;
    JSR.W (.pointers,X)                                                  ;90800E;
    LDA.W Pose                                                           ;908011;
    CMP.W #$004D                                                         ;908014;
    BEQ .neutralJump                                                     ;908017;
    CMP.W #$004E                                                         ;908019;
    BEQ .neutralJump                                                     ;90801C;
    LDA.W SamusAnimationFrameTimer                                       ;90801E;
    DEC                                                                  ;908021;
    STA.W SamusAnimationFrameTimer                                       ;908022;
    BEQ +                                                                ;908025;
    BPL .return                                                          ;908027;

+   LDA.W SamusAnimationFrame                                            ;908029;
    INC                                                                  ;90802C;
    STA.W SamusAnimationFrame                                            ;90802D;
    BRA .handleDelay                                                     ;908030;

  .neutralJump:
    LDA.W SamusYDirection                                                ;908032;
    CMP.W #$0002                                                         ;908035;
    BEQ +                                                                ;908038;
    LDA.W SamusAnimationFrame                                            ;90803A;
    CMP.W #$0001                                                         ;90803D;
    BNE +                                                                ;908040;
    LDA.W SamusAnimationFrameTimer                                       ;908042;
    CMP.W #$0001                                                         ;908045;
    BNE +                                                                ;908048;
    LDA.W #$0004                                                         ;90804A;
    STA.W SamusAnimationFrameTimer                                       ;90804D;

+   LDA.W SamusAnimationFrameTimer                                       ;908050;
    DEC                                                                  ;908053;
    STA.W SamusAnimationFrameTimer                                       ;908054;
    BEQ .incAnimFrame                                                    ;908057;
    BPL .return                                                          ;908059;

  .incAnimFrame:
    LDA.W SamusAnimationFrame                                            ;90805B;
    INC                                                                  ;90805E;
    STA.W SamusAnimationFrame                                            ;90805F;

  .handleDelay:
    JSR.W Handle_Samus_AnimationDelay                                    ;908062;

  .return:
    PLP                                                                  ;908065;
    RTS                                                                  ;908066;

  .pointers:
    dw AnimateSamus_FX_None                                              ;908067;
    dw AnimateSamus_FX_Lava                                              ;908069;
    dw AnimateSamus_FX_Acid                                              ;90806B;
    dw AnimateSamus_FX_Water                                             ;90806D;
    dw RTS_908077                                                        ;90806F;
    dw RTS_908077                                                        ;908071;
    dw RTS_908077                                                        ;908073;
    dw RTS_908077                                                        ;908075;


;;; $8077: RTS ;;;
RTS_908077:
; 8: Spores
; Ah: Rain
; Ch: Fog
; 28h: Ceres Ridley
; 2Ah: Ceres elevator
; 2Ch: Haze
    RTS                                                                  ;908077;


;;; $8078: Animate Samus - FX = none ;;;
