; Called by the subroutines for the other FX types too if the FX doesn't affect Samus
    LDA.W XSpeedDivisor                                                  ;908078;
    STA.W SamusAnimationFrameBuffer                                      ;90807B;
    LDA.W LiquidPhysicsType                                              ;90807E;
    BEQ .return                                                          ;908081;
    BIT.W #$0001                                                         ;908083;
    BNE +                                                                ;908086;
    STZ.W LiquidPhysicsType                                              ;908088;
    RTS                                                                  ;90808B;

+   STZ.W LiquidPhysicsType                                              ;90808C;
    LDA.W #$000E                                                         ;90808F;
    JSL.L QueueSound_Lib2_Max6                                           ;908092;
    LDA.W SuitPaletteIndex                                               ;908096;
    BIT.W #$0004                                                         ;908099;
    BNE .goto_Spawn_WaterSplash_AirBubbles                               ;90809C;
    LDA.W MovementType                                                   ;90809E;
    AND.W #$00FF                                                         ;9080A1;
    CMP.W #$0003                                                         ;9080A4;
    BEQ +                                                                ;9080A7;
    CMP.W #$0014                                                         ;9080A9;
    BNE .goto_Spawn_WaterSplash_AirBubbles                               ;9080AC;

+   LDA.W #$0030                                                         ;9080AE;
    JSL.L QueueSound_Lib1_Max6                                           ;9080B1;

  .goto_Spawn_WaterSplash_AirBubbles:
    BRA Spawn_WaterSplash_AirBubbles                                     ;9080B5;

  .return:
    RTS                                                                  ;9080B7;


