    REP #$30                                                             ;9082E6;
    LDY.W SamusAnimationFrame                                            ;9082E8;
    LDA.W Pose                                                           ;9082EB;
    ASL                                                                  ;9082EE;
    TAX                                                                  ;9082EF;
    LDA.L AnimationDelayTable,X                                          ;9082F0;
    STA.B DP_Temp00                                                      ;9082F4;
    LDA.B [DP_Temp00],Y                                                  ;9082F6;
    AND.W #$00FF                                                         ;9082F8;
    BIT.W #$0080                                                         ;9082FB;
    BNE +                                                                ;9082FE;
    JSR.W Handle_NormalAnimationDelay                                    ;908300;
    BRA .return                                                          ;908303;

+   JSR.W Handle_SpeedBooster_AnimationDelay                             ;908305;
    TAX                                                                  ;908308;
