    AND.W #$000F                                                         ;90830B;
    ASL                                                                  ;90830E;
    TAX                                                                  ;90830F;
    JSR.W (.pointers,X)                                                  ;908310;
    BCC .return                                                          ;908313;
    LDA.B [DP_Temp00],Y                                                  ;908315;
    AND.W #$00FF                                                         ;908317;
    CLC                                                                  ;90831A;
