    AND.W #$00FF                                                         ;90854D;
    CMP.W #$0001                                                         ;908550;
    BEQ .speedBoosterEquipped                                            ;908553;
    JMP.W .finish                                                        ;908555;

  .speedBoosterEquipped:
    LDA.W EquippedItems                                                  ;908558;
    BIT.W #$2000                                                         ;90855B;
    BNE .speedBoostTimer                                                 ;90855E;
    LDY.W #$0000                                                         ;908560;
    STY.W SamusAnimationFrame                                            ;908563;
    LDA.L AnimationDelayTable_Running_NoSpeedBooster_pointer             ;908566;
    STA.B DP_Temp00                                                      ;90856A;
    LDA.B [DP_Temp00],Y                                                  ;90856C;
    AND.W #$00FF                                                         ;90856E;
    CLC                                                                  ;908571;
    ADC.W SamusAnimationFrameBuffer                                      ;908572;
    STA.W SamusAnimationFrameTimer                                       ;908575;
    LDA.W #$0000                                                         ;908578;
    BRA .return                                                          ;90857B;

  .speedBoostTimer:
    LDA.W SamusBoostTimer                                                ;90857D;
    DEC                                                                  ;908580;
    STA.W SamusBoostTimer                                                ;908581;
    BIT.W #$00FF                                                         ;908584;
    BNE .finish                                                          ;908587;
    LDA.W SamusBoostTimer                                                ;908589;
    BIT.W #$0400                                                         ;90858C;
    BNE +                                                                ;90858F;
    CLC                                                                  ;908591;
    ADC.W #$0100                                                         ;908592;
