+   XBA                                                                  ;9085AA;
    AND.W #$00FF                                                         ;9085AB;
    ASL                                                                  ;9085AE;
    TAX                                                                  ;9085AF;
    LDA.W SamusBoostTimer                                                ;9085B0;
    AND.W #$FF00                                                         ;9085B3;
    ORA.L SpeedBoostTimerResetValues,X                                   ;9085B6;
    STA.W SamusBoostTimer                                                ;9085BA;
    LDY.W #$0000                                                         ;9085BD;
    STY.W SamusAnimationFrame                                            ;9085C0;
    LDA.L AnimationDelayTable_Running_SpeedBooster_pointers,X            ;9085C3;
    STA.B DP_Temp00                                                      ;9085C7;
    LDA.B [DP_Temp00],Y                                                  ;9085C9;
