;;; $9A7E: Calculate Samus X base speed - deceleration allowed ;;;
CalculateSamusXBaseSpeed_DecelerationAllowed:
;; Parameters:
;;     X: Samus X speed table entry pointer
;;         [[X] + 0].[[X] + 2]: X acceleration
;;         [[X] + 4].[[X] + 6]: Max X speed
;;         [[X] + 8].[[X] + Ah]: X deceleration
;; Returns:
;;     $12.$14: Samus X base speed
    PHP                                                                  ;909A7E;
    REP #$30                                                             ;909A7F;
    LDA.W SamusXAccelerationMode                                         ;909A81;
    BNE .decelerating                                                    ;909A84;
    LDA.W SamusXBaseSubSpeed                                             ;909A86;
    CLC                                                                  ;909A89;
    ADC.W $0002,X                                                        ;909A8A;
    STA.W SamusXBaseSubSpeed                                             ;909A8D;
    LDA.W SamusXBaseSpeed                                                ;909A90;
    ADC.W $0000,X                                                        ;909A93;
    STA.W SamusXBaseSpeed                                                ;909A96;
    CMP.W $0004,X                                                        ;909A99;
    BMI .return                                                          ;909A9C;
    BEQ .checkMaxSubSpeed                                                ;909A9E;

  .capXSpeed:
    LDA.W $0004,X                                                        ;909AA0;
    STA.W SamusXBaseSpeed                                                ;909AA3;
    LDA.W $0006,X                                                        ;909AA6;
    STA.W SamusXBaseSubSpeed                                             ;909AA9;
    BRA .return                                                          ;909AAC;

  .checkMaxSubSpeed:
    LDA.W SamusXBaseSubSpeed                                             ;909AAE;
    CMP.W $0006,X                                                        ;909AB1;
    BMI .return                                                          ;909AB4;
    BEQ .return                                                          ;909AB6;
    BRA .capXSpeed                                                       ;909AB8;

  .decelerating:
    SEP #$20                                                             ;909ABA;
    LDA.W SamusXDecelerationMultiplier                                   ;909ABC;
    BEQ .zeroMultiplier                                                  ;909ABF;
    STA.W $4202                                                          ;909AC1;
    LDA.W $000B,X                                                        ;909AC4;
    STA.W $4203                                                          ;909AC7;
    NOP                                                                  ;909ACA;
    NOP                                                                  ;909ACB;
    NOP                                                                  ;909ACC;
