;;; $9B1F: Calculate Samus X base speed - deceleration disallowed ;;;
CalculateSamusXBaseSpeed_DecelerationDisallowed:
;; Parameters:
;;     X: Samus X speed table entry pointer
;;         [[X] + 0].[[X] + 2]: X acceleration
;;         [[X] + 4].[[X] + 6]: Max X speed
;;         [[X] + 8].[[X] + Ah]: X deceleration
;; Returns:
;;     Carry: Set if reached max X speed, otherwise clear
;;     $12.$14: Samus X base speed
    PHP                                                                  ;909B1F;
    REP #$30                                                             ;909B20;
    LDA.W SamusXAccelerationMode                                         ;909B22;
    BIT.W #$0001                                                         ;909B25;
    BNE .turningAround                                                   ;909B28;
    LDA.W SamusXBaseSubSpeed                                             ;909B2A;
    CLC                                                                  ;909B2D;
    ADC.W $0002,X                                                        ;909B2E;
    STA.W SamusXBaseSubSpeed                                             ;909B31;
    LDA.W SamusXBaseSpeed                                                ;909B34;
    ADC.W $0000,X                                                        ;909B37;
    STA.W SamusXBaseSpeed                                                ;909B3A;
    CMP.W $0004,X                                                        ;909B3D;
    BMI .returnClear                                                     ;909B40;
    BEQ .checkMaxSubSpeed                                                ;909B42;

  .capXSpeed:
    LDA.W $0004,X                                                        ;909B44;
    STA.W SamusXBaseSpeed                                                ;909B47;
    LDA.W $0006,X                                                        ;909B4A;
    STA.W SamusXBaseSubSpeed                                             ;909B4D;
    BRA .returnSet                                                       ;909B50;

  .checkMaxSubSpeed:
    LDA.W SamusXBaseSubSpeed                                             ;909B52;
    CMP.W $0006,X                                                        ;909B55;
    BMI .returnClear                                                     ;909B58;
    BEQ .returnClear                                                     ;909B5A;
    BRA .capXSpeed                                                       ;909B5C;

  .turningAround:
    SEP #$20                                                             ;909B5E;
    LDA.W SamusXDecelerationMultiplier                                   ;909B60;
    BEQ .zeroMultiplier                                                  ;909B63;
    STA.W $4202                                                          ;909B65;
    LDA.W $000B,X                                                        ;909B68;
    STA.W $4203                                                          ;909B6B;
    NOP                                                                  ;909B6E;
    NOP                                                                  ;909B6F;
    NOP                                                                  ;909B70;
    REP #$20                                                             ;909B71;
    LDA.W $4216                                                          ;909B73;
    STA.B DP_Temp14                                                      ;909B76;
    SEP #$20                                                             ;909B78;
    LDA.W $0008,X                                                        ;909B7A;
    STA.W $4203                                                          ;909B7D;
    NOP                                                                  ;909B80;
    NOP                                                                  ;909B81;
    NOP                                                                  ;909B82;
    REP #$20                                                             ;909B83;
    LDA.W $4217                                                          ;909B85;
    AND.W #$00FF                                                         ;909B88;
    STA.B DP_Temp12                                                      ;909B8B;
    BRA +                                                                ;909B8D;

  .zeroMultiplier:
    REP #$20                                                             ;909B8F;
    LDA.W $000A,X                                                        ;909B91;
    STA.B DP_Temp14                                                      ;909B94;
    LDA.W $0008,X                                                        ;909B96;
    STA.B DP_Temp12                                                      ;909B99;

+   LDA.W SamusXBaseSubSpeed                                             ;909B9B;
    SEC                                                                  ;909B9E;
    SBC.B DP_Temp14                                                      ;909B9F;
    STA.W SamusXBaseSubSpeed                                             ;909BA1;
    LDA.W SamusXBaseSpeed                                                ;909BA4;
    SBC.B DP_Temp12                                                      ;909BA7;
    STA.W SamusXBaseSpeed                                                ;909BA9;
    BPL .returnClear                                                     ;909BAC;
    STZ.W SamusXBaseSpeed                                                ;909BAE;
    STZ.W SamusXBaseSubSpeed                                             ;909BB1;
    STZ.W SamusXAccelerationMode                                         ;909BB4;

  .returnClear:
    LDA.W SamusXBaseSpeed                                                ;909BB7;
    STA.B DP_Temp12                                                      ;909BBA;
    LDA.W SamusXBaseSubSpeed                                             ;909BBC;
    STA.B DP_Temp14                                                      ;909BBF;
    PLP                                                                  ;909BC1;
    CLC                                                                  ;909BC2;
    RTS                                                                  ;909BC3;

  .returnSet:
    LDA.W SamusXBaseSpeed                                                ;909BC4;
    STA.B DP_Temp12                                                      ;909BC7;
    LDA.W SamusXBaseSubSpeed                                             ;909BC9;
    STA.B DP_Temp14                                                      ;909BCC;
    PLP                                                                  ;909BCE;
    SEC                                                                  ;909BCF;
    RTS                                                                  ;909BD0;


