    RTL                                                                  ;90A940;

+   STZ.B DP_Temp2E                                                      ;90A941;
    LDA.W SamusXPosition                                                 ;90A943;
    AND.W #$FF00                                                         ;90A946;
    XBA                                                                  ;90A949;
