    REP #$30                                                             ;90AB76;
    LDA.B DP_Temp30                                                      ;90AB78;
    AND.W #$00FF                                                         ;90AB7A;
    TAX                                                                  ;90AB7D;
    CLC                                                                  ;90AB7E;
    ADC.B DP_Temp0F                                                      ;90AB7F;
    STA.B DP_Temp09                                                      ;90AB81;
    SEP #$20                                                             ;90AB83;
    LDA.W MapTilesExplored,X                                             ;90AB85;
    STA.B DP_Temp2C                                                      ;90AB88;
    LDA.B [DP_Temp09]                                                    ;90AB8A;
    STA.B DP_Temp2D                                                      ;90AB8C;
    LDA.B #$20                                                           ;90AB8E;
    CMP.B DP_Temp22                                                      ;90AB90;
    BNE .leftMapPage1                                                    ;90AB92;
    LDA.B DP_Temp2C                                                      ;90AB94;
    STA.B DP_Temp19                                                      ;90AB96;
    LDA.B DP_Temp2D                                                      ;90AB98;
    STA.B DP_Temp27                                                      ;90AB9A;
