    STA.B DP_Temp26                                                      ;90ABA4;

+   LDA.W MapTilesExplored+4,X                                           ;90ABA6;
    STA.B DP_Temp2C                                                      ;90ABA9;
    REP #$20                                                             ;90ABAB;
