    LDA.B #$20                                                           ;90ABBB;
    CMP.B DP_Temp22                                                      ;90ABBD;
    BNE .leftMapPage2                                                    ;90ABBF;
    LDA.B DP_Temp2C                                                      ;90ABC1;
