;;; $C4B5: Handle switching HUD selection ;;;
HandleSwitchingHUDSelection:
    PHP                                                                  ;90C4B5;
    REP #$30                                                             ;90C4B6;
    LDA.W SelectedHUDItem                                                ;90C4B8;
    STA.B DP_Temp12                                                      ;90C4BB;
    LDA.B DP_Controller1New                                              ;90C4BD;
    BIT.W ItemCancelBinding                                              ;90C4BF;
    BEQ .notItemCancel                                                   ;90C4C2;
    STZ.W AutoCancelHUDItemIndex                                         ;90C4C4;
    BRA .itemCancel                                                      ;90C4C7;

  .notItemCancel:
    LDA.B DP_Controller1Input                                            ;90C4C9;
    BIT.W ItemCancelBinding                                              ;90C4CB;
    BNE +                                                                ;90C4CE;
    STZ.B DP_Temp16                                                      ;90C4D0;
    BRA .checkItemSelect                                                 ;90C4D2;

+   LDA.W #$0001                                                         ;90C4D4;
    STA.B DP_Temp16                                                      ;90C4D7;

  .checkItemSelect:
    LDA.B DP_Controller1New                                              ;90C4D9;
    BIT.W ItemSelectBinding                                              ;90C4DB;
    BEQ .itemSelectEnd                                                   ;90C4DE;
    LDA.W SelectedHUDItem                                                ;90C4E0;
    INC                                                                  ;90C4E3;
    CMP.W #$0006                                                         ;90C4E4;
    BMI .itemCancelEnd                                                   ;90C4E7;

  .itemCancel:
    LDA.W #$0000                                                         ;90C4E9;

  .itemCancelEnd:
    STA.W SelectedHUDItem                                                ;90C4EC;

  .loop:
    ASL                                                                  ;90C4EF;
    TAX                                                                  ;90C4F0;
    JSR.W (.pointers,X)                                                  ;90C4F1;
    BCC .checkHoldingItemCancel                                          ;90C4F4;
    LDA.W SelectedHUDItem                                                ;90C4F6;
    INC                                                                  ;90C4F9;
    STA.W SelectedHUDItem                                                ;90C4FA;
    CMP.W #$0006                                                         ;90C4FD;
    BMI .loop                                                            ;90C500;
    LDA.W #$0000                                                         ;90C502;
    STA.W SelectedHUDItem                                                ;90C505;
    BRA .loop                                                            ;90C508;

  .checkHoldingItemCancel:
    LDA.B DP_Temp16                                                      ;90C50A;
    BEQ .resetAutoCancel                                                 ;90C50C;
    LDA.W SelectedHUDItem                                                ;90C50E;
    STA.W AutoCancelHUDItemIndex                                         ;90C511;
    BRA .itemSelectEnd                                                   ;90C514;

  .resetAutoCancel:
    STZ.W AutoCancelHUDItemIndex                                         ;90C516;

  .itemSelectEnd:
    LDA.W SelectedHUDItem                                                ;90C519;
    CMP.B DP_Temp12                                                      ;90C51C;
    BNE .itemChanged                                                     ;90C51E;
    LDA.W ArmCannonToggleFlag                                            ;90C520;
    INC                                                                  ;90C523;
    CMP.W #$0003                                                         ;90C524;
    BMI .toggleArmCannon                                                 ;90C527;
    LDA.W #$0002                                                         ;90C529;

  .toggleArmCannon:
    STA.W ArmCannonToggleFlag                                            ;90C52C;
    BRA .return                                                          ;90C52F;

  .itemChanged:
    LDA.W #$0001                                                         ;90C531;
    STA.W ArmCannonToggleFlag                                            ;90C534;

  .return:
    PLP                                                                  ;90C537;
    RTS                                                                  ;90C538;

  .pointers:
; Switched to HUD item handlers
    dw SwitchedToHUDItemHandler_Nothing                                  ;90C539; Nothing
    dw SwitchedToHUDItemHandler_Missiles                                 ;90C53B; Missiles
    dw SwitchedToHUDItemHandler_SuperMissiles                            ;90C53D; Super missiles
    dw SwitchedToHUDItemHandler_PowerBombs                               ;90C53F; Power bombs
    dw SwitchedToHUDItemHandler_GrappleBeam                              ;90C541; Grapple
