;;; $CC39: Calculate X/Y components of radius [A], angle [Y] ;;;
Math_90CC39:
;; Parameters:
;;     A: Radius
;;     Y: Angle. Origin = up, positive direction = clockwise
;; Returns:
;;     $14: X component of radius
;;     $16: Y component of radius

; Clone of Calculate_XY_ComponentsOf_RadiusA_AngleY
    PHP                                                                  ;90CC39;
    REP #$30                                                             ;90CC3A;
    PHX                                                                  ;90CC3C;
    STA.B DP_Temp18                                                      ;90CC3D;
    TYA                                                                  ;90CC3F;
    STA.B DP_Temp1A                                                      ;90CC40;
    CMP.W #$0080                                                         ;90CC42;
    BPL .greaterThanEqualTo80                                            ;90CC45;
    ASL                                                                  ;90CC47;
    TAX                                                                  ;90CC48;
    JSR.W Math_90CC8A                                                    ;90CC49;
    BRA +                                                                ;90CC4C;

  .greaterThanEqualTo80:
    SEC                                                                  ;90CC4E;
    SBC.W #$0080                                                         ;90CC4F;
    AND.W #$00FF                                                         ;90CC52;
    ASL                                                                  ;90CC55;
    TAX                                                                  ;90CC56;
    JSR.W Math_90CC8A                                                    ;90CC57;
    EOR.W #$FFFF                                                         ;90CC5A;
    INC                                                                  ;90CC5D;

+   STA.B DP_Temp14                                                      ;90CC5E;
    LDA.B DP_Temp1A                                                      ;90CC60;
    SEC                                                                  ;90CC62;
    SBC.W #$0040                                                         ;90CC63;
    AND.W #$00FF                                                         ;90CC66;
    CMP.W #$0080                                                         ;90CC69;
    BPL .AGreaterThanEqualTo80                                           ;90CC6C;
    ASL                                                                  ;90CC6E;
    TAX                                                                  ;90CC6F;
    JSR.W Math_90CC8A                                                    ;90CC70;
    BRA +                                                                ;90CC73;

  .AGreaterThanEqualTo80:
    SEC                                                                  ;90CC75;
    SBC.W #$0080                                                         ;90CC76;
    AND.W #$00FF                                                         ;90CC79;
    ASL                                                                  ;90CC7C;
    TAX                                                                  ;90CC7D;
    JSR.W Math_90CC8A                                                    ;90CC7E;
    EOR.W #$FFFF                                                         ;90CC81;
    INC                                                                  ;90CC84;

+   STA.B DP_Temp16                                                      ;90CC85;
    PLX                                                                  ;90CC87;
    PLP                                                                  ;90CC88;
    RTS                                                                  ;90CC89;


;;; $CC8A: A = [$18] * sin([X] / 2 * pi / 80h) ;;;
Math_90CC8A:
;; Parameters:
;;     X: Angle * 2
;;     $18: Radius
